window.predict = () => {
const input = tf.tidy(() => {
    return tf.image.resizeBilinear(		//转换图像tensor尺寸
        tf.browser.fromPixels(canvas),	//canvas转换为tensor
        [28, 28],	//转换成28*28
        true
    ).slice([0, 0, 0], [28, 28, 1])	//canvas图片是彩色图片，通过slice转换为黑白图片
    .toFloat()	//训练数据进行过归一化，因此预测值也要归一化
    .div(255)	//归一化
    .reshape([1, 28, 28, 1]);	//和神经网络第一层的输入格式统一
});
