  <div id="home">
    <div>count：{{count}}</div>
    <div>count1：{{count1}}</div>
    <div>count3：{{count3}}</div>
    <div>count4：{{count4}}</div>
    <div>count5：{{count5}}</div>
    <button @click="increment">+</button>
    <button @click="decrement">-</button>
  </div>
</template>

